import { Component } from '@angular/core';
import { Staff } from '../staff-list/staff.model';

@Component({
  selector: 'app-staff-list',
  templateUrl: './staff-list.component.html',
  styleUrls: ['./staff-list.component.css']
})
export class StaffListComponent {
  staffList: Staff[] = [
    { staffFullName: 'John Doe', staffUserName: 'jdoe', status: true },
    { staffFullName: 'Jane Smith', staffUserName: 'jsmith', status: false },
    { staffFullName: 'Bob Johnson', staffUserName: 'bjohnson', status: true }
  ];

  toggleLoginStatus(staff: Staff) {
    staff.status = !staff.status;
  }

}
